// Program to calculate the sum of  first 10 number

public class AssignmentP2 {
	public static void main (String[] args) {
		// TODO Auto-generated method stub
		
		int i, num = 10, sum = 0;  
		
		for(i = 1; i <= num; ++i)  
		{  
		  
		sum = sum + i;  
		}  
		  
		System.out.println("Sum of First 10 Natural Numbers is = " + sum);  
	}

}
